mapper.select:
SELECT

FROM t_sys_file sys_file_
WHERE sys_file_.id = #{id,jdbcType=BIGINT}

SELECT

FROM t_sys_file sys_file_
WHERE sys_file_.code = #{code,jdbcType=VARCHAR}

mapper.update:
UPDATE t_sys_file
`name` = #{name,jdbcType=VARCHAR}, suffix = #{suffix,jdbcType=VARCHAR}, version = #{version,jdbcType=INTEGER}, code = #{code,jdbcType=VARCHAR}, `key` = #{key,jdbcType=VARCHAR}, `size` = #{size,jdbcType=BIGINT}, url = #{url,jdbcType=VARCHAR}, note = #{note,jdbcType=VARCHAR}, check_type = #{checkType,jdbcType=VARCHAR}, check_code = #{checkCode,jdbcType=VARCHAR}, state = #{state,jdbcType=INTEGER}, create_date = #{createDate,jdbcType=TIMESTAMP}, update_date = #{updateDate,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}
